public class Part3_2 {
    public double cgRatio(String DNA) {
        int curIndex = 0;
        double count = 0.0;
        while (curIndex < DNA.length()) {
            char c = DNA.charAt(curIndex);
            if (c == 'C' || c == 'G') {
                count++;
            }
            curIndex++;
        }
        return (double) count / DNA.length();
    }

    public int countCTG(String DNA) {
        int count = 0;
        int startIndex = 0;
        while (true) {
            int curIndex = DNA.indexOf("CTG", startIndex);
            if (curIndex == -1) {
                break;
            }
            count++;
            startIndex = DNA.indexOf("CTG", startIndex) + "CTG".length();
        }
        return count;
    }

    public void test() {
        System.out.println(cgRatio("ATGCCATAG"));
        System.out.println(countCTG(
                ""));
    }

    public static void main(String[] args) {
        new Part3_2().test();
    }
}
